import React, { useState, useEffect, useRef } from 'react';
import { HubConnection, HubConnectionBuilder, LogLevel } from '@microsoft/signalr';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'; // –°–æ–∑–¥–∞–¥–∏–º —ç—Ç–æ—Ç —Ñ–∞–π–ª –ø–æ–∑–∂–µ
import { ChatMessageDto, CreateRoomRequest, JoinRoomRequest } from './types';

const App: React.FC = () => {
    // –°–æ—Å—Ç–æ—è–Ω–∏–µ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏—è
    const [connection, setConnection] = useState<HubConnection | null>(null);

    // –°–æ—Å—Ç–æ—è–Ω–∏–µ UI
    const [messages, setMessages] = useState<ChatMessageDto[]>([]);
    const [username, setUsername] = useState('');
    const [roomId, setRoomId] = useState('');
    const [roomName, setRoomName] = useState(''); // –î–ª—è —Å–æ–∑–¥–∞–Ω–∏—è
    const [isInRoom, setIsInRoom] = useState(false);
    const [inputText, setInputText] = useState('');

    // –°—Å—ã–ª–∫–∞ –Ω–∞ div —Å —Å–æ–æ–±—â–µ–Ω–∏—è–º–∏ –¥–ª—è –∞–≤—Ç–æ-—Å–∫—Ä–æ–ª–ª–∞
    const messagesEndRef = useRef<HTMLDivElement>(null);

    // 1. –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è —Å–æ–µ–¥–∏–Ω–µ–Ω–∏—è
    useEffect(() => {
        const newConnection = new HubConnectionBuilder()
            // üëá –ò–ó–ú–ï–ù–ò–¢–ï –≠–¢–£ –°–¢–†–û–ö–£ (–±—ã–ª–æ 5000, —Å—Ç–∞–ª–æ 5157)
            .withUrl("http://localhost:5157/chatHub") 
            .withAutomaticReconnect()
            .configureLogging(LogLevel.Information)
            .build();

        setConnection(newConnection);
    }, []);

    // 2. –ù–∞—Å—Ç—Ä–æ–π–∫–∞ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–æ–≤ —Å–æ–±—ã—Ç–∏–π (–ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ connection)
    useEffect(() => {
        if (connection) {
            connection.start()
                .then(() => {
                    console.log('Connected to SignalR Hub');

                    // –û–±—Ä–∞–±–æ—Ç–∫–∞ –≤—Ö–æ–¥—è—â–∏—Ö —Å–æ–æ–±—â–µ–Ω–∏–π
                    connection.on("ReceiveMessage", (message: ChatMessageDto) => {
                        setMessages(prev => [...prev, message]);
                    });

                    // –û–±—Ä–∞–±–æ—Ç–∫–∞ –∏—Å—Ç–æ—Ä–∏–∏ —Å–æ–æ–±—â–µ–Ω–∏–π (–ø—Ä–∏ –≤—Ö–æ–¥–µ)
                    connection.on("ReceiveRoomHistory", (history: ChatMessageDto[]) => {
                        setMessages(history);
                    });

                    // –û–±—Ä–∞–±–æ—Ç–∫–∞ –æ—à–∏–±–æ–∫
                    connection.on("ReceiveError", (errorMsg: ChatMessageDto) => {
                        alert(`Error: ${errorMsg.text}`);
                        setIsInRoom(false); // –í—ã–∫–∏–¥—ã–≤–∞–µ–º –æ–±—Ä–∞—Ç–Ω–æ –≤ –ª–æ–±–±–∏
                    });
                })
                .catch(e => console.log('Connection failed: ', e));
        }
    }, [connection]);

    // –ê–≤—Ç–æ-—Å–∫—Ä–æ–ª–ª –≤–Ω–∏–∑ –ø—Ä–∏ –Ω–æ–≤–æ–º —Å–æ–æ–±—â–µ–Ω–∏–∏
    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    // --- –§—É–Ω–∫—Ü–∏–∏ –¥–µ–π—Å—Ç–≤–∏–π ---

    const createRoom = async () => {
        if (!username) return alert("Enter username!");
        try {
            const request: CreateRoomRequest = { username, roomName };
            // –ë—ç–∫–µ–Ω–¥ –≤–æ–∑–≤—Ä–∞—â–∞–µ—Ç roomId (—Å—Ç—Ä–æ–∫—É)
            const newRoomId = await connection?.invoke("CreateRoom", request);
            setRoomId(newRoomId);
            setIsInRoom(true);
            setMessages([]); // –û—á–∏—Å—Ç–∫–∞ –ø—Ä–∏ –Ω–æ–≤–æ–º —Å–æ–∑–¥–∞–Ω–∏–∏
        } catch (e) {
            console.error(e);
        }
    }

    const joinRoom = async () => {
        if (!username || !roomId) return alert("Enter username and Room ID!");
        try {
            const request: JoinRoomRequest = { username, roomId };
            // –ë—ç–∫–µ–Ω–¥ –≤–æ–∑–≤—Ä–∞—â–∞–µ—Ç bool
            const success = await connection?.invoke("JoinRoom", request);
            if (success) {
                setIsInRoom(true);
            }
        } catch (e) {
            console.error(e);
        }
    }

    const sendMessage = async () => {
        if (!inputText) return;
        try {
            const msg: ChatMessageDto = {
                user: username,
                text: inputText,
                type: 'text',
                timestamp: '' // –ë—ç–∫–µ–Ω–¥ —Å–∞–º –ø—Ä–æ—Å—Ç–∞–≤–∏—Ç –≤—Ä–µ–º—è
            };
            await connection?.invoke("SendMessageToRoom", msg);
            setInputText('');
        } catch (e) {
            console.error(e);
        }
    }

    const leaveRoom = async () => {
        try {
            await connection?.invoke("LeaveRoom");
            setIsInRoom(false);
            setMessages([]);
        } catch (e) {
            console.error(e);
        }
    }

    const deleteRoom = async () => {
        if(!window.confirm("Are you sure? Only the creator can delete the room.")) return;
        try {
            await connection?.invoke("DeleteRoom", roomId, username);
            // –ï—Å–ª–∏ —É—Å–ø–µ—Ö, —Å–µ—Ä–≤–µ—Ä –ø—Ä–∏—à–ª–µ—Ç –≤—Å–µ–º (–≤–∫–ª—é—á–∞—è –Ω–∞—Å) —Å–æ–æ–±—â–µ–Ω–∏–µ System,
            // –∞ –∑–∞—Ç–µ–º —É–¥–∞–ª–∏—Ç –∏–∑ –≥—Ä—É–ø–ø—ã.
            // –ù–æ –º—ã –º–æ–∂–µ–º –∏ –ø—Ä–∏–Ω—É–¥–∏—Ç–µ–ª—å–Ω–æ –≤—ã–π—Ç–∏ –≤ UI:
            setIsInRoom(false);
            setMessages([]);
        } catch (e) {
            console.error(e);
        }
    }

    // --- –†–µ–Ω–¥–µ—Ä–∏–Ω–≥ ---

    return (
        <div className="container mt-5">
            {!isInRoom ? (
                // --- LOBBY SCREEN ---
                <div className="card p-4 mx-auto" style={{maxWidth: '500px'}}>
                    <h2 className="text-center mb-4">SignalR Chat</h2>

                    <div className="mb-3">
                        <label className="form-label">Username</label>
                        <input 
                            className="form-control" 
                            placeholder="Enter your name"
                            value={username}
                            onChange={e => setUsername(e.target.value)} 
                        />
                    </div>

                    <hr />

                    {/* Join Existing */}
                    <h5>Join Room</h5>
                    <div className="input-group mb-3">
                        <input 
                            className="form-control" 
                            placeholder="Room ID"
                            value={roomId}
                            onChange={e => setRoomId(e.target.value)}
                        />
                        <button className="btn btn-primary" onClick={joinRoom}>Join</button>
                    </div>

                    <hr />

                    {/* Create New */}
                    <h5>Create Room</h5>
                    <div className="mb-2">
                        <input 
                            className="form-control" 
                            placeholder="Room Name (Optional)"
                            value={roomName}
                            onChange={e => setRoomName(e.target.value)}
                        />
                    </div>
                    <button className="btn btn-success w-100" onClick={createRoom}>Create New Room</button>
                </div>
            ) : (
                // --- CHAT ROOM SCREEN ---
                <div className="card mx-auto" style={{maxWidth: '800px', height: '80vh'}}>
                    <div className="card-header d-flex justify-content-between align-items-center bg-primary text-white">
                        <h5 className="mb-0">Room: {roomId}</h5>
                        <div>
                            <button className="btn btn-sm btn-danger me-2" onClick={deleteRoom}>Delete Room</button>
                            <button className="btn btn-sm btn-warning" onClick={leaveRoom}>Leave</button>
                        </div>
                    </div>

                    <div className="card-body overflow-auto d-flex flex-column" style={{flex: 1, backgroundColor: '#f8f9fa'}}>
                        {messages.map((msg, idx) => {
                            const isMyMessage = msg.user === username;
                            const isSystem = msg.type === 'system' || msg.type === 'join' || msg.type === 'leave';

                            if (isSystem) {
                                return (
                                    <div key={idx} className="text-center text-muted small my-2">
                                        <em>{msg.text} - {msg.timestamp}</em>
                                    </div>
                                );
                            }

                            return (
                                <div key={idx} className={`d-flex mb-3 ${isMyMessage ? 'justify-content-end' : 'justify-content-start'}`}>
                                    <div 
                                        className={`card px-3 py-2 ${isMyMessage ? 'bg-primary text-white' : 'bg-white'}`}
                                        style={{maxWidth: '70%', borderRadius: '15px'}}
                                    >
                                        <div className="small fw-bold mb-1" style={{opacity: 0.8}}>{msg.user}</div>
                                        <div>{msg.text}</div>
                                        <div className="small text-end mt-1" style={{opacity: 0.7, fontSize: '0.7em'}}>{msg.timestamp}</div>
                                    </div>
                                </div>
                            );
                        })}
                        <div ref={messagesEndRef} />
                    </div>

                    <div className="card-footer">
                        <div className="input-group">
                            <input 
                                type="text" 
                                className="form-control"
                                placeholder="Type a message..."
                                value={inputText}
                                onChange={e => setInputText(e.target.value)}
                                onKeyDown={e => e.key === 'Enter' && sendMessage()}
                            />
                            <button className="btn btn-primary" onClick={sendMessage}>Send</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default App;
